A.fnStr:
(packet,mem,log)=>{

	if(packet.data)
	{
		log(` ${packet.data} received at A`)
	}
  	log(`Sending data request to SERVER`)
	let newpacket={
	sender:"A",
	destination:"SERVER"
  }
	return [
	{
		to:"SERVER",
		packet:newpacket
	}
	]
}

SERVER.fnStr:
(packet,mem,log)=>{
		if(mem.buffer_count===0)
	{
		log(`BUFFER OVERFLOW! Denial of Service for request from ${packet.sender} `);
		return [];
	}
  if(packet.sender in mem.router_table)
	{	
	  let newpacket={
		data:"DATA BY SERVER",
	  }
	  log(`Sending data to ${packet.sender}`);
		return [
		{
			to:'reply',
			packet:newpacket
		}]
	}	

	mem.buffer_count=mem.buffer_count-1;
	return [];

}

