		let n=mem.public_key.n;
		let priv=mem.private_key;
	if(!packet.key_exchanged)
	{		


		let public_pass_key=Math.pow(e,priv)%n;	
		let newpacket={
			destination:packet.destination,
			key_exchanged:public_pass_key
		}
	log(`sending public pass key ${public_pass_key} to B`);
		return [
		{to:mem.router_table[newpacket.destination],packet:newpacket}
		]
	}
	else
	{
		let secret=Math.pow(packet.key_exchanged,priv)%n;
		log(`Secret key ${secret} generated at A .`);
     return [];
	}
}

