	{
	log(`public pass key ${public_pass} tracked for A ---> B by MIM`);
	}
	else
	{
	log(`public pass key ${public_pass} tracked for B ---> A by MIM`);		
	}
	return [
		{	
		to:mem.router_table[packet.destination],
		packet:packet
		}
	]
}

